const Title = styled.h1`
  text-align: center;
  border: 1px solid green;
`

function App() {
  return (
    <div className="App">
      <Title>Mini blog</Title>
      <Routes>
        <Route path='/write' element={<PostWritePage />} />
        <Route index element={<MainPage />} />
        <Route path='/post/:postId' element={<PostViewPage />} />
      </Routes>
    </div>
  );
}

export default App;
