import data from "../../Data";

const PostWritePage = () => {
  const [value, setValue] = useState('');
  const [dummy, setDummy] = useState(data);
  const [title, setTitle] = useState('');

  const handleChangeTitle = (event) => {
    setTitle(event.target.value);
  }

  return (
    <>
      <form>
        <div>글제목
          <input onChange={handleChangeTitle} id="title" type="text" name="title" placeholder="제목을 입력해주세요"></input>
        </div>
        <textarea name="story" type="text" placeholder="내용을 입력해주세요"></textarea>
        <section>
          <input type="submit" value="작성완료"></input>
        </section>
      </form>
    </>
  );
};

